import React from "react";
import './Header.css'
import Picture from './AgriAceLogo.png'
class Header extends React.Component
                                     {

render()                                        
  {
 const about= useRef(null);
 const services= useRef(null);
 const contact= useRef(null);

 const scrollToSection= (elementRef)=>{
    window.scrollTo({
        top:elementRef.current.offsetTop,
        behavior:'smooth'
      });
    return (
        <div>

        <header> 

      <nav class="navbar">
      <ul class="navbar-nav">
        <li class="HEADER">
          <a class="header" href="#" onClick={()=> scrollToSection(about)}>About Us</a>
        </li>
        <li class="HEADER">
          <a class="header" href="#" onClick={()=> scrollToSection(services)}>Services</a>

        </li>
        <li class="HEADER">
          <a class="header" href="#" onClick={()=> scrollToSection(contact)}>Contact</a>
        </li>
        <li class="HEADER">
          <a class="header" href="#"><button>Login</button></a>
        </li>
      </ul>
      <img src={Picture}  alt="./AgriAceLogo.png" className="logo" ></img>
    </nav>
    </header>


    <div className='hero'>
        <ul>
          <h2 onClick={()=> scrollToSection(about)} id='abouts'>About Us</h2>
          <h2  onClick={()=> scrollToSection(services)} id='serves'>Services</h2>
          <h2 onClick={()=> scrollToSection(contact)} id='contat'>Contact Us</h2>
          <img src={image} alt='./AgriAceLgo.png' className='logo'></img>

          <button className="login"><a href="/Login">Login</a></button>

        </ul>
        <img src={seed} alt='./SEEDS.png' className='seed'></img>
      </div>


    </div>      
    )
  }
}
  }
export default Header;
